import React from 'react'
import { Form, Button, Segment } from 'semantic-ui-react'
import { Formik, Field, ErrorMessage } from 'formik'
import * as Yup from 'yup'
import axios from 'axios'

const Registration = () => {
    const handleSubmit = (values, { setSubmitting }) => {
        axios.post('http://localhost:3000/registration', values)
            .then(response => {
                console.log(response)
                setSubmitting(false)
                // redirect to home page or display success message
            })
            .catch(error => {
                console.log(error)
                setSubmitting(false)
                // display error message
            })
    }

    const validationSchema = Yup.object().shape({
        username: Yup.string().required('Username is required'),
        email: Yup.string().email('Invalid email').required('Email is required'),
        password: Yup.string().min(8, 'Password must be at least 8 characters').required('Password is required')
    })

    return (
        <Segment>
            <Formik 
                initialValues={{ username: '', email: '', password: '' }}
                validationSchema={validationSchema}
                onSubmit={handleSubmit}
            >
                {({ handleSubmit, isSubmitting }) => (
                    <Form onSubmit={handleSubmit}>
                        <Form.Field>
                            <label>Username</label>
                            <Field type='text' name='username' placeholder='Enter your username' />
                            <ErrorMessage name='username' component='div' className='error' />
                        </Form.Field>
                        <Form.Field>
                            <label>Email</label>
                            <Field type='email' name='email' placeholder='Enter your email' />
                            <ErrorMessage name='email' component='div' className='error' />
                        </Form.Field>
                        <Form.Field>
                            <label>Password</label>
                            <Field type='password' name='password' placeholder='Enter your password' />
                            <ErrorMessage name='password' component='div' className='error' />
                        </Form.Field>
                        <Button type='submit' disabled={isSubmitting}>Submit</Button>
                        <Button type='button' onClick={() => {/* navigate to login page */}}>Already have an account? Login</Button>
                    </Form>
                )}
            </Formik>
        </Segment>
    )
}

export default Registration
