import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router';
import { blogList } from '../../components/common/data';
import Chip from '../../components/common/Chip';
import EmptyList from '../../components/common/EmptyList';
import './styles.css';
import { Link } from 'react-router-dom';
import {Button, List} from "semantic-ui-react"
import EditArticle from '../EditArticle/EditArticle';
import axios from 'axios';

const Blog = () => {
  const { id } = useParams();
  const [blog, setBlog] = useState(null);
  useEffect(() => {
    let blog = blogList.find((blog) => blog.id === parseInt(id));
    if (blog) {
      setBlog(blog);
    }
  }, []);

    const [articles, setArticles] = useState([]);
    const [editing, setEditing] = useState(false);
    const [selectedArticle, setSelectedArticle] = useState({});

    useEffect(() => {
        axios.get('http://localhost:3000/blogList')
            .then(res => {
                setArticles(res.data);
                let blog = articles.find((blog) => blog.id === parseInt(id));
                if (blog) {
                  setA(blog);
                }
            })
    }, []);

    const handleEdit = (article) => {
        setSelectedArticle(article);
        setEditing(true);
    }

  return (
    <>
      <Link className='blog-goBack' to={`/item/${id}`}>
        <span> &#8592;</span> <span>Go Back</span>
      </Link>
      {blog ? (
        <div className='blog-wrap'>
          <header>
            <p className='blog-date'>Published {blog.createdAt}</p>
            <h1>{blog.title}</h1>
            <div className='blog-subCategory'>
              {blog.subCategory.map((category, i) => (
                <div key={i}>
                  <Chip label={category} />
                </div>
              ))}
            </div>
          </header>
          <img src={blog.cover} alt='cover' />
          <p className='blog-desc'>{blog.description}</p>
          <Button type='button'>
            <Link to={`/articles/${id}`}>Edit Article</Link>
          </Button>
          <div>
            {editing ? 
                <EditArticle 
                    article={selectedArticle}
                    setEditing={setEditing}
                /> : 
                <div>
                    <List divided relaxed>
                        {articles.map((article) => (
                            <List.Item key={article.id}>
                                <List.Content>
                                    <List.Header as='a'>{article.title}</List.Header>
                                    <List.Description>{article.content}</List.Description>
                                </List.Content>
                                <Button
                                onClick={() => handleEdit(article)}
                                >Edit</Button>
                            </List.Item>
                        ))}
                    </List>
                </div>
            }
          </div>
        </div>
      ) : (
        <EmptyList />
      )}
    </>
  );
};
export default Blog;
