import React, { useState, useEffect } from 'react';
import { useHttp } from '../components/common/useHttp';
import SearchBar from './../components/SearchBar/SearchBar';
import Article from './../pages/';

const Home = () => {
  const [isSearching, setIsSearching] = useState(false);
  const [searchResults, setSearchResults] = useState([]);
  const { data: articles } = useHttp('http://localhost:3000/blogList');

  const handleSearch = async (searchTerm) => {
    setIsSearching(true);
    const results = articles.filter((article) => {
      return article.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        article.category.toLowerCase().includes(searchTerm.toLowerCase()) ||
        article.subCategory.toLowerCase().includes(searchTerm.toLowerCase()) ||
        article.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
        article.authorName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        article.createdAt.toLowerCase().includes(searchTerm.toLowerCase());
    });
    setSearchResults(results);
  };

  return (
    <div>
      <SearchBar onSearch={handleSearch} />
      {isSearching ? (
        <div>
          {searchResults.map((article) => (
            <Article key={article.createdAt} article={article} />
          ))}
        </div>
      ) : (
        <div>
          {articles.map((article) => (
            <Article key={article.createdAt} article={article} />
          ))}
        </div>
      )}
    </div>
  );
};

export default Home;
