import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import { Button, Form as SUIForm } from 'semantic-ui-react';
import axios from 'axios';

const RegisterSchema = Yup.object().shape({
  username: Yup.string()
    .min(5, 'Username must be at least 5 characters')
    .required('Username is required'),
  email: Yup.string()
    .email('Invalid email')
    .required('Email is required'),
  password: Yup.string()
    .min(8, 'Password must be at least 8 characters')
    .required('Password is required')
});

const Register = () => {
  return (
    <div className="form-container">
      <Formik
        initialValues={{
          username: '',
          email: '',
          password: '',
        }}
        validationSchema={RegisterSchema}
        onSubmit={(values, { setSubmitting, resetForm }) => {
          axios.post('http://localhost:3000/register', values)
            .then(res => {
              console.log(res);
              setSubmitting(false);
              resetForm();
              //redirect to home page
              //this.props.history.push('/home');
            })
            .catch(err => {
              console.log(err);
              setSubmitting(false);
            });
        }}
      >
        {({ isSubmitting, isValid }) => (
          <Form as={SUIForm}>
            <SUIForm.Field>
              <label>Username</label>
              <Field type="text" name="username" placeholder="Enter your username" />
              <ErrorMessage name="username" component="div" />
            </SUIForm.Field>
            <SUIForm.Field>
              <label>Email</label>
              <Field type="email" name="email" placeholder="Enter your email" />
              <ErrorMessage name="email" component="div" />
            </SUIForm.Field>
            <SUIForm.Field>
              <label>Password</label>
              <Field type="password" name="password" placeholder="Enter your password" />
              <ErrorMessage name="password" component="div" />
            </SUIForm.Field>
            <Button type="submit" disabled={!isValid || isSubmitting}>Submit</Button>
            <Button type="button" onClick={() => this.props.history.push('/login')}>Already have an account? Login</Button>
          </Form>
        )}
      </Formik>
    </div>
  );
};

export default Register;
