import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const EditArticle = (props) => {
    const [article, setArticle] = useState(props.article);
    const { id } = useParams();

    useEffect(() => {
        setArticle(props.article);
    }, [props.article]);

    const handleChange = (event) => {
        const { name, value } = event.target;
        setArticle({ ...article, [name]: value });
    }

    const handleSave = () => {
        // Code to update the JSON file with the new title and content
        fetch(`http:localhost/${id}`, {
            method: 'PUT',
            body: JSON.stringify(article),
            headers: { 'Content-Type': 'application/json' }
        })
        .then(res => res.json())
        .then(data => {
            // handle success
        })
        .catch(error => {
            // handle error
        });
    }

    return (
        <div>
            <input name="title" value={article.title} onChange={handleChange} />
            <textarea name="content" value={article.content} onChange={handleChange} />
            <button onClick={handleSave}>Save</button>
        </div>
    );
}

export default EditArticle;
