import React from 'react';
import { Card, Image, Icon, Grid } from 'semantic-ui-react';

const Article = (props) => {
  return (
    <Card className="article-card">
      <Card.Content>
        <Card.Header className="center aligned">{props.title}</Card.Header>
        <Card.Meta>
          <Grid columns={2}>
            <Grid.Row>
              <Grid.Column>
                <Icon name='folder' />{props.category}
              </Grid.Column>
              <Grid.Column>
                <Icon name='folder' />{props.subCategory}
              </Grid.Column>
            </Grid.Row>
          </Grid>
        </Card.Meta>
        <Card.Description>
          {props.description}
        </Card.Description>
      </Card.Content>
      <Card.Content extra>
        <Grid columns={2}>
          <Grid.Row>
            <Grid.Column>
              <Icon name='user' />{props.authorName}
            </Grid.Column>
            <Grid.Column>
              <Icon name='time' />{props.createdAt}
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </Card.Content>
    </Card>
  );
};

export default Article;
